import React, { useEffect, useState } from "react";
import "./styles.css";
import gql from "graphql-tag";
import request from "./utils/request";
import Ships from "./components/Ships/Ships";

export default function App() {
    const [ships, setShips] = useState([]);
    const fetchShips = async () => {
        const response = await request(gql`
      {
        ships {
          name
          home_port
          image
        }
      }
    `);
        setShips(response.data.ships);
    };

    useEffect(() => {
        fetchShips();
    }, []);


    const [inputValue, setInputValue] = useState('');
    const [filterData, setFilterData] = useState({});

    useEffect(() => {
        const filterShips = ships.filter(ship => ship.name.toUpperCase() === inputValue);
        setShips(filterShips)
    }, [inputValue, ships])

    const handleOnChange = (e) => {
        setInputValue(e.target.value.toUpperCase())
    }


    const handleSubmit = (e) => {
        e.preventDefault()
    }

    return (
        <div className="App">
            <div className="logo">
                <img src="https://i.ibb.co/wWTf5xD/logo.png" alt="" />
            </div>
            <div className="ship_container">
                <div className="search_box">
                    <form onClick={handleSubmit}>
                        <input onChange={handleOnChange} placeholder="Search Shipss" type="text" />
                        <button type="submit">Search</button>
                    </form>
                </div>
                <h2 className="total_count">Total Count : {ships.length}</h2>

                <div>
                    {
                        ships.map(ship => <Ships
                            key={ship.name}
                            ship={ship}
                        ></Ships>)
                    }
                </div>
            </div>
        </div>
    );
}
