import React, { useEffect, useState } from "react";
import "./styles.css";
import gql from "graphql-tag";
import request from "./utils/request";

export default function App() {
    const [ships, setShips] = useState([]);
    const fetchShips = async () => {
        const response = await request(gql`
      {
        ships {
          name
          home_port
          image
        }
      }
    `);
        setShips(response.data.ships);
    };

    useEffect(() => {
        fetchShips();
    }, []);


    const [inputValue, setInputValue] = useState('');
    const [filterData, setFilterData] = useState({});

    useEffect(() => {
        const filterShips = ships.filter(ship => ship.name.toUpperCase() == inputValue);
        setFilterData(filterShips)
    }, [inputValue, ships])

    const handleOnChange = (e) => {
        setInputValue(e.target.value.toUpperCase())
    }

    console.log(inputValue, ships)
    console.log(filterData)

    const handleSubmit = (e) => {
        e.preventDefault()
    }

    return (
        <div className="App">
            <div className="logo">
                <img src="" alt="" />
            </div>
            <div>
                <form onClick={handleSubmit}>
                    <input onChange={handleOnChange} type="text" />
                    <button type="submit">Search</button>
                </form>
            </div>
            <h2>Total Count : {ships.length}</h2>
        </div>
    );
}
